            ['kode' => '04.07', 'parent_kode' => '04', 'nama' => 'Laporan Penjualan', 'url' => 'kasir.laporan.penjualan', 'icon' => '<span class="svg-icon menu-icon"><svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" width="24px" height="24px" viewBox="0 0 24 24" version="1.1"> <g stroke="none" stroke-width="1" fill="none" fill-rule="evenodd"> <polygon points="0 0 24 0 24 24 0 24"/> <path d="M5.85714286,2 L13.7364114,2 C14.0910962,2 14.4343066,2.12568431 14.7051108,2.35473959 L19.4686994,6.3839416 C19.8056532,6.66894833 20,7.08787823 20,7.52920201 L20,20.0833333 C20,21.8738751 19.9795521,22 18.1428571,22 L5.85714286,22 C4.02044787,22 4,21.8738751 4,20.0833333 L4,3.91666667 C4,2.12612489 4.02044787,2 5.85714286,2 Z" fill="#000000" fill-rule="nonzero" opacity="0.3"/> <rect fill="#000000" x="6" y="11" width="9" height="2" rx="1"/> <rect fill="#000000" x="6" y="15" width="5" height="2" rx="1"/> </g> </svg></span>'],
        );
        FiturProgram::insert($user_fitur_programs);

        foreach ($admin_fitur_programs as $key => $fitur_program) {
            HakAkses::create([
                'user_level_id' => 1,
                'fitur_program_id' => $key+1,
                'flag_akses' => 1
            ]);
        }
        foreach ($user_fitur_programs as $key => $fitur_program) {
            HakAkses::create([
                'user_level_id' => 2,
                'fitur_program_id' => count($admin_fitur_programs) + ($key+1),
                'flag_akses' => 1
            ]);
        }

        $free_lisensi = Lisensi::create([
            'nama' => 'Gratis',
            'harga' => 0,
            'keterangan' => 'Lisensi gratis untuk semua pengguna'
        ]);
        $profiles = Profil::factory()->count(7)->create();
        foreach ($profiles as $profile) {
            LisensiProfil::create([
                'lisensi_id' => $free_lisensi->id,
                'profil_id' => $profile->id,
                'no_lisensi' => Str::uuid(),
                'berlaku_dari' => date('Y-m-d')
            ]);
            $users = User::factory()->count(2)->create();
            foreach ($users as $user) {
                UserProfil::create([
                    'profil_id' => $profile->id,
                    'user_id' => $user->id
                ]);
            }
            $products = Produk::factory()->count(rand(20, 30))->create([
                'profil_id' => $profile->id
            ]);
            $penjualans = Penjualan::factory()->count(rand(30, 50))->create([
                'profil_id' => $profile->id,
            ]);

            foreach ($penjualans as $penjualan) {
                $list_product_id = $products->pluck('id')->toArray();
                $list_product_harga = $products->pluck('harga')->toArray();
                for ($i = 1; $i <= rand(3, 10); $i++) {
                    $index_produk = array_rand($list_product_id);
                    $total_harga = 0;
                    DetailPenjualan::factory()->count(1)->create([
                        'penjualan_id' => $penjualan->id,
                        'produk_id' => $list_product_id[$index_produk],
                        'harga' => $list_product_harga[$index_produk],
                    ]);
                    $total_harga += $list_product_harga[$index_produk];
                }
                if ($penjualan->is_bayar == 1)
                    Penjualan::find($penjualan->id)->update([
                        'total' => $total_harga,
                        'dibayar' => $total_harga,
                    ]);
            }
        }
    }
